ктронной почты <a href=\"mailto:papyshkina.irina@yandex.ru\">papyshkina.irina@yandex.ru</a>, адрес сайта 40307-s-018.edusite.ru\";s:6:\"filter\";b:1;s:6:\"visual\";b:1;s:11:\"panels_info\";a:6:{s:5:\"class\";s:14:\"WP_Widget_Text\";s:4:\"grid\";i:0;s:4:\"cell\";i:1;s:2:\"id\";i:1;s:9:\"widget_id\";s:36:\"7573702f-d815-4ecc-9512-2505c4177fc3\";s:5:\"style\";a:2:{s:27:\"background_image_attachment\";b:0;s:18:\"background_display\";s:4:\"tile\";}}}}s:5:\"grids\";a:1:{i:0;a:4:{s:5:\"cells\";i:2;s:5:\"style\";a:2:{s:18:\"background_display\";s:4:\"tile\";s:14:\"cell_alignment\";s:10:\"flex-start\";}s:5:\"ratio\";d:0.41421355999999998;s:15:\"ratio_direction\";s:5:\"right\";}}s:10:\"grid_cells\";a:2:{i:0;a:4:{s:4:\"grid\";i:0;s:5:\"index\";i:0;s:6:\"weight\";d:0.70710678237309998;s:5:\"style\";a:0:{}}i:1;a:4:{s:4:\"grid\";i:0;s:5:\"index\";i:1;s:6:\"weight\";d:0.29289321762690002;s:5:\"style\";a:0:{}}}}'),(326,124,'panels_data','a:3:{s:7:\"widgets\";a:2:{i:0;a:5:{s:6:\"frames\";a:3:{i:0;a:9:{s:16:\"background_image\";i:0;s:25:\"background_image_fallback\";s:0:\"\";s:16:\"background_color\";b:0;s:21:\"background_image_type\";s:5:\"cover\";s:16:\"foreground_image\";i:88;s:25:\"foreground_image_fallback\";s:0:\"\";s:3:\"url\";s:0:\"\";s:17:\"background_videos\";a:0:{}s:10:\"new_window\";b:0;}i:1;a:9:{s:16:\"background_image\";i:120;s:25:\"background_image_fallback\";s:0:\"\";s:16:\"background_color\";b:0;s:21:\"background_image_type\";s:5:\"cover\";s:16:\"foreground_image\";i:0;s:25:\"foreground_image_fallback\";s:0:\"\";s:3:\"url\";s:0:\"\";s:17:\"background_videos\";a:0:{}s:10:\"new_window\";b:0;}i:2;a:9:{s:16:\"background_image\";i:119;s:25:\"background_image_fallback\";s:0:\"\";s:16:\"background_color\";b:0;s:21:\"background_image_type\";s:5:\"cover\";s:16:\"foreground_image\";i:0;s:25:\"foreground_image_fallback\";s:0:\"\";s:3:\"url\";s:0:\"\";s:17:\"background_videos\";a:0:{}s:10:\"new_window\";b:0;}}s:8:\"controls\";a:7:{s:5:\"speed\";i:800;s:7:\"timeout\";i:8000;s:13: