ра, с. Фроловское','','inherit','closed','closed','','155-revision-v1','','','2017-12-26 00:29:47','2017-12-25 21:29:47','',155,'http://cr42072.tmweb.ru/2017/12/26/155-revision-v1/',0,'revision','',0),(167,1,'2017-12-26 00:29:47','2017-12-25 21:29:47','<div id=\"pl-155\"  class=\"panel-layout\" ><div id=\"pg-155-0\"  class=\"panel-grid panel-no-style\" ><div id=\"pgc-155-0-0\"  class=\"panel-grid-cell\"  data-weight=\"1\" ><div id=\"panel-155-0-0-0\" class=\"so-panel widget widget_sow-slider panel-first-child panel-last-child\" data-index=\"0\" data-style=\"{&quot;background_image_attachment&quot;:false,&quot;background_display&quot;:&quot;tile&quot;}\" >[siteorigin_widget class=\"SiteOrigin_Widget_Slider_Widget\"]<input type=\"hidden\" value=\"{&quot;instance&quot;:{&quot;frames&quot;:[{&quot;background_image&quot;:164,&quot;background_image_fallback&quot;:&quot;&quot;,&quot;background_color&quot;:false,&quot;background_image_type&quot;:&quot;cover&quot;,&quot;foreground_image&quot;:0,&quot;foreground_image_fallback&quot;:&quot;&quot;,&quot;url&quot;:&quot;&quot;,&quot;background_videos&quot;:[],&quot;new_window&quot;:false},{&quot;background_image&quot;:165,&quot;background_image_fallback&quot;:&quot;&quot;,&quot;background_color&quot;:false,&quot;background_image_type&quot;:&quot;cover&quot;,&quot;foreground_image&quot;:0,&quot;foreground_image_fallback&quot;:&quot;&quot;,&quot;url&quot;:&quot;&quot;,&quot;background_videos&quot;:[],&quot;new_window&quot;:false},{&quot;background_image&quot;:160,&quot;background_image_fallback&quot;:&quot;&quot;,&quot;background_color&quot;:false,&quot;background_image_type&quot;:&quot;cover&quot;,&quot;foreground_image&quot;:0,&quot;foreground_image_fallback&quot;:&quot;&quot;,&quot;url&quot;:&quot;&quot;,&quot;background_videos&quot;:[],&quot;new_window&quot;:false}],&quot;controls&quot;:{&quot;speed&quot;:800,&quot;timeout&quot;:8000,&quot;nav_color_hex&quot;:&quot;#FFFFFF&quot;,&quot;nav_style&quot;:&quot;thin&quot;,&quot;nav